/*
-- Query: SELECT * FROM rt.movies
LIMIT 0, 1000

-- Date: 2019-01-31 18:55
*/
CREATE TABLE IF NOT EXISTS `movies` (
  `id` int(11) NOT NULL,
  `title` varchar(255) DEFAULT NULL,
  `title_url` varchar(255) DEFAULT NULL,
  `tmdb_poster_path` varchar(255) DEFAULT NULL,
  `tmdb_backdrop_path` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


/*
-- Query: SELECT * FROM rt.critics
LIMIT 0, 1000

-- Date: 2019-02-05 17:27
*/
CREATE TABLE IF NOT EXISTS`critics` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `top_critic` tinyint(1) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  `img_url` varchar(255) DEFAULT NULL,
  `organization` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=452 DEFAULT CHARSET=latin1;



/*
-- Query: SELECT *  FROM rt.critic_reviews
LIMIT 0, 500

-- Date: 2019-02-02 21:38
*/
CREATE TABLE `critic_reviews` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `review_date` date DEFAULT NULL,
  `fresh` tinyint(1) DEFAULT NULL,
  `review_text` text CHARACTER SET utf8 NOT NULL,
  `movie_id` int(11) DEFAULT NULL,
  `critic_id` int(11) DEFAULT NULL,
  `score` float(8,2) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `critic_reviews_movie_id_foreign` (`movie_id`),
  CONSTRAINT `critic_reviews_movie_id_foreign` FOREIGN KEY (`movie_id`) REFERENCES `movies` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=19884 DEFAULT CHARSET=latin1;

